eetype = / usr / local / freetype / <br /> # Echo $? <br /> # Make; make install <br /><br /> 6, install the latest Curl library <br /> # Cd / root / soft <br /> # Tar-zxf curl-7.15.0.tar.gz <br /> #. / Configure - prefix = / usr / local / curl <br /> # Echo $? <br /> # Make; make install <br /> 7 <br /> Libxml2 php5 support the need, so install libxml2 <br /> # Cd / root / soft <br /> # Tar-zxf libxml2-2.6.19.tar.gz <br /> # Cd libxml2-2.6.19 <br /> #. / Configure - prefix = / usr/local/libxml2 <br /> # Echo $? <br /> # Make; make install <br /><br /> 8, install libxslt <br /> # Cd / root / soft <br /> # Tar-zxf libxslt-1.1.15.tar.gz <br /> # Cd libxslt-1.1.15 <br /> #. / Configure - prefix = / usr / local / libxslt - with-libxml-prefix = / usr/local/libxml2 <br /> # Echo $? <br /> # Make; make install <br /> <strong>To install php</strong> <br /> # Tar-zxf php-5.0.5.tar.gz <br /> # Cd php-5.05 <br /> #. / Configure - prefix = / usr / local / php - enable-mbstring - with-apxs2 = / usr/local/apache2/bin/apxs - with-pear = / usr / share / php - with -bz2 - with-mysql = / usr / local / mysql / - with-curl = / usr / local / curl - enable-ftp - with-libxml-dir = / usr/local/libxml2 - with- expat-dir = / usr / lib - enable-soap - with-xsl = / usr / local / libxslt - enable-xslt - with-gd = / usr/local/gd2 / - enable-gd-native -ttf - enable-gd-jis-conv - with-jpeg-dir = / usr/local/jpeg6 / - with-zlib-dir = / usr / lib - with-png - with-freetype-dir = / usr / local / freetype - with-config-file-path = / etc - with-iconv - disable-ipv6 - enable-static - enable-maintainer-zts - enable-zend-multibyte - enabl e-inline-optimization - enable-zend-multibyte - enable-sockets - enable-soap</font></span> </p><p align=\"left\" class=\"MsoNormal\" style=\"text-align: left; margin: 0cm 0cm 0pt; mso-pagination: widow-orphan\"> <font size=\"2\"><span lang=\"EN-US\" style=\"font-family: Verdana; font-size: 9pt; mso-font-kerning: 0pt; mso-bidi-font-family: Ã¥Â®Â‹Ã¤Â½Â“\"># Echo $? <br /> 